import React from "react";
import Loading from "../LoadingError/Loading";
import Message from "../LoadingError/Error";
import { Link } from "react-router-dom";
import moment from "moment";

const Transactions = (/*props*/) => {
  const { loading, error, transactions } = props;
  return (
    <div className=" d-flex justify-content-center align-items-center flex-column">
      {loading ? (
        <Loading />
      ) : error ? (
        <Message variant="alert-danger">{error}</Message>
      ) : (
        <>
          {transactions.length === 0 ? (
            <div className="col-12 alert alert-info text-center mt-3">
              No transactions
              <Link
                className="btn btn-success mx-2 px-3 py-2"
                to="/"
                style={{
                  fontSize: "12px",
                }}
              >
                START SHOPPING
              </Link>
            </div>
          ) : (
            <div className="table-responsive">
              <table className="table">
                <thead>
                  <tr>
                    <th>ID</th>
                    <th>STATUS</th>
                    <th>DATE</th>
                    <th>TOTAL</th>
                  </tr>
                </thead>
                <tbody>
                  {transactions.map((transaction) => (
                    <tr
                      className={`${
                        transaction.isPaid ? "alert-success" : "alert-danger"
                      }`}
                      key={transaction._id}
                    >
                      <td>
                        <a
                          href={`/transaction/${transaction._id}`}
                          className="link"
                        >
                          {transaction._id}
                        </a>
                      </td>
                      <td>{transaction.isPaid ? <>Paid</> : <>Not Paid</>}</td>
                      <td>
                        {transaction.isPaid
                          ? moment(transaction.paidAt).calendar()
                          : moment(transaction.createdAt).calendar()}
                      </td>
                      <td>${transaction.totalPrice}</td>
                    </tr>
                  ))}
                </tbody>
              </table>
            </div>
          )}
        </>
      )}
    </div>
  );
};

export default Transactions;
