import axios from "axios";
import {
  TRANSFER_ADD_FAIL,
  TRANSFER_ADD_REQUEST,
  TRANSFER_ADD_SUCCESS,
} from "../Constants/TransferConstants";

// ADD TRANSFER
export const addTransfer =
  (name, address, city, accountNumber, amount) =>
  async (dispatch, getState) => {
    try {
      dispatch({
        type: TRANSFER_ADD_REQUEST,
      });

      const {
        userLogin: { userInfo },
      } = getState();

      const config = {
        headers: {
          Accept: "application/json",
          "Content-Type": "application/json",
          Authorization: `Bearer ${userInfo.token}`,

          "Access-Control-Allow-Origin": "https://developer.mozilla.org",
          Vary: "Origin",
        },
      };
      const { data } = await axios.post(
        "http://localhost:5000/api/transactions",
        { name, address, city, amount, accountNumber },
        config
      );
      dispatch({ type: TRANSFER_ADD_SUCCESS, payload: data });
      localStorage.setItem("transactionItems", JSON.stringify(data));
    } catch (error) {
      dispatch({
        type: TRANSFER_ADD_FAIL,
        payload:
          error.response && error.response.data.message
            ? error.response.data.message
            : error.message,
      });
    }
  };
