import React from "react";
import Loading from "../LoadingError/Loading";
import Message from "../LoadingError/Error";
//import { Link } from "react-router-dom";
import moment from "moment";
import "./../../Styles/components/homeTabs.scss";
import { FontAwesomeIcon } from "@fortawesome/react-fontawesome";
import {
  faCircleChevronRight,
  faArrowDownLong,
} from "@fortawesome/free-solid-svg-icons";

const HomeTabs = (props) => {
  const { loading, error, transactions, userInfo } = props;

  return (
    <div className=" d-flex justify-content-center align-items-center flex-column">
      {loading ? (
        <Loading />
      ) : error ? (
        <Message variant="alert-danger">{error}</Message>
      ) : (
        <>
          <div className="container mt-5">
            <div className="row align-items-start mb-5">
              <div className="col-5 account">
                <p className="acc-title">Citizen current account</p>
                <p className="acc-num">
                  account number: {userInfo.accountNumber}
                </p>
              </div>
              <div className="col-6">
                <div className="balance-widget">
                  <p className="available-balance">
                    <span>available balance:</span>
                    <br />${userInfo.balance + 1000}
                  </p>

                  <p className="current-balance">
                    <span>Balance:</span>
                    <br />${userInfo.balance}
                  </p>
                </div>
              </div>
              <div className="col-1">
                <a href="#">
                  <FontAwesomeIcon
                    className="bnt-details"
                    icon={faCircleChevronRight}
                  ></FontAwesomeIcon>
                  <span>Details</span>
                </a>
              </div>
            </div>
            <div className="table-responsive">
              <table className="table table-striped ">
                <thead>
                  <tr>
                    <h5>LAST TRANSACTIONS</h5>
                  </tr>
                </thead>
                <tbody>
                  {transactions.length === 0 ? (
                    <tr className="col alert alert-info text-center mt-3">
                      No transactions
                    </tr>
                  ) : (
                    <>
                      {transactions.map((transaction, index) => (
                        <tr key={index}>
                          <th scope="row" className="col-1">
                            <FontAwesomeIcon
                              className="icon arrow"
                              icon={faArrowDownLong}
                            ></FontAwesomeIcon>
                          </th>
                          <th>
                            <p>{moment(transaction.createdAt).calendar()}</p>
                          </th>
                          <th>
                            <p>
                              Purpose of payment: {transaction.purposeOfPayment}
                            </p>
                          </th>
                          <th>
                            <p>User name: {transaction.name}</p>
                          </th>
                          <th>
                            <p>${transaction.amount}</p>
                          </th>
                        </tr>
                      ))}
                    </>
                  )}
                </tbody>
              </table>
            </div>
            <div className="align-items-end">
              <button>All transactions</button>
            </div>
          </div>
        </>
      )}
    </div>
  );
};

export default HomeTabs;
