import React from "react";
import Loading from "../LoadingError/Loading";
import Message from "../LoadingError/Error";
//import { Link } from "react-router-dom";
import moment from "moment";
import "./../../Styles/components/homeTabs.scss";
import { FontAwesomeIcon } from "@fortawesome/react-fontawesome";
import {
  faChevronRight,
  faArrowDownLong,
} from "@fortawesome/free-solid-svg-icons";
import { useSelector } from "react-redux";

const HomeTabs = () => {
  const transactionList = useSelector((state) => state.transactionListMy);
  const { loading, error, transactions } = transactionList;

  return (
    <div className=" d-flex justify-content-center align-items-center flex-column">
      {loading ? (
        <Loading />
      ) : error ? (
        <Message variant="alert-danger">{error}</Message>
      ) : (
        <>
          <div className="container mt-5">
            <div className="row align-items-start mb-5">
              <div className="col-5 account">
                <select
                  className="form-select form-select-lg mb-3"
                  aria-label=".form-select-lg example"
                >
                  <option selected>
                    <h3>Citizen current account</h3>
                    <p>account number: 258</p>
                  </option>
                  <option value="1">
                    <h3>Citizen current account</h3>
                    <p>account number: 147</p>
                  </option>
                  <option value="2">
                    <h3>Citizen current account</h3>
                    <p>account number: 369</p>
                  </option>
                  <option value="3">
                    <h3>Citizen current account</h3>
                    <p>account number: 789</p>
                  </option>
                </select>
              </div>
              <div className="col-3">
                <div className="account-balance">
                  <p>account balance:</p>
                  <h2>$1.589</h2>
                </div>
              </div>
              <div className="col-3">
                <div className="balance">
                  <p>account balance:</p>
                  <h2>$589</h2>
                </div>
              </div>
              <div className="col-1">
                <div className="btn-details">
                  <div className="circle">
                    <FontAwesomeIcon
                      className="icon arrow"
                      icon={faChevronRight}
                    ></FontAwesomeIcon>
                  </div>
                </div>
              </div>
            </div>
            <div className="row">
              <h5>LAST TRANSACTIONS</h5>
              {transactions.length === 0 ? (
                <div className="col alert alert-info text-center mt-3">
                  No transactions
                </div>
              ) : (
                <>
                  {transactions.map((transaction, index) => (
                    <div className="row align-items-start" key={index}>
                      <div className="col-3 d-flex">
                        <FontAwesomeIcon
                          className="icon arrow"
                          icon={faArrowDownLong}
                        ></FontAwesomeIcon>
                        <p>{moment(transaction.createdAt).calendar()}</p>
                      </div>
                      <div className="col-6">
                        <p>
                          uplata za: PLACANJE KAZNE; korisnicko ime:{" "}
                          {transaction.name}
                        </p>
                      </div>
                      <div className="col-3">
                        <p>-$56</p>
                      </div>
                    </div>
                  ))}
                </>
              )}
            </div>
            <div className="align-items-end">
              <button>All transactions</button>
            </div>
          </div>
        </>
      )}
    </div>
  );
};

export default HomeTabs;
