import React, { useState } from "react";
import Loading from "../LoadingError/Loading";
import Message from "../LoadingError/Error";
//import { Link } from "react-router-dom";
//import moment from "moment";
import { useDispatch, useSelector } from "react-redux";
import { addTransfer } from "../../Redux/Actions/TransferAction";
import { updateUserProfile } from "../../Redux/Actions/UserAction";

const Transactions = (props) => {
  const { loading, error, userInfo } = props;

  const [name, setName] = useState("");
  const [address, setAddress] = useState("");
  const [city, setCity] = useState("");
  const [accountNumber, setAccountNumber] = useState("");
  const [amount, setAmount] = useState(Number);
  const [purposeOfPayment, setPurposeOfPayment] = useState("");
  const initialState = "";

  const dispatch = useDispatch();

  const userDetails = useSelector((state) => state.userDetails);
  const user = userDetails;
  const userUpdateProfile = useSelector((state) => state.userUpdateProfile);
  const { loading: updateLoading } = userUpdateProfile;

  const submitHandler = (e) => {
    e.preventDefault();
    dispatch(
      addTransfer(name, address, city, accountNumber, amount, purposeOfPayment)
    );
    dispatch(updateUserProfile({ id: user._id, amount }));
    setName(initialState);
    setAddress(initialState);
    setCity(initialState);
    setAccountNumber(initialState);
    setAmount(initialState);
    setPurposeOfPayment(initialState);
  };

  return (
    <div className=" d-flex justify-content-center align-items-center flex-column">
      {loading ? (
        <Loading />
      ) : error ? (
        <Message variant="alert-danger">{error}</Message>
      ) : (
        <>
          <form className="container mt-5 p-0" onSubmit={submitHandler}>
            <div className="align-items-start mb-5 d-flex my-acc">
              <div className="col-6 align-items-start ">
                <div className="widget-inner">
                  <label className="label-1">Source account</label>
                  <div className="acc-container">
                    <p className="title-main">Current account of citizen</p>
                    <p className="acc-num">
                      account number: {userInfo.accountNumber}
                    </p>
                  </div>
                  <div className="custom-table">
                    <div className="accountInfo">
                      <div className="table-row">
                        <div className="col-6">
                          <p className="col-text text-size-5 text-cl-2 tx-upper left">
                            Balance:
                          </p>
                        </div>
                        <div className="col-6">
                          <p className="col-text text-size-4 text-cl-2 right">
                            ${userInfo.balance}
                          </p>
                        </div>
                      </div>
                      <div className="table-row row-margin-1">
                        <div className="col-6">
                          <p className="col-text text-size-5 text-cl-2 tx-upper left">
                            Available balance:
                          </p>
                        </div>
                        <div className="col-6">
                          <p className="col-text text-size-4 text-cl-2 right">
                            ${userInfo.balance + 1000}
                          </p>
                        </div>
                      </div>
                    </div>
                  </div>
                </div>
              </div>
              <div className="col-6 align-items-start">
                <div className="row">
                  <h5 className="col-6">Name:</h5>
                  <h5 className="col-6">Admin</h5>
                </div>
                <div className="row">
                  <h5 className="col-6">Address:</h5>
                  <h5 className="col-6">PETI PRIGRADSKI PUT BB</h5>
                </div>
                <div className="row">
                  <h5 className="col-6">City:</h5>
                  <h5 className="col-6">BANJALUKA</h5>
                </div>
                <div className="row">
                  <h5 className="col-6">Purpose of payment:</h5>
                  <h5 className="col-6">
                    <input
                      className="input-acc"
                      type="text"
                      value={purposeOfPayment}
                      onChange={(e) => {
                        setPurposeOfPayment(e.target.value);
                      }}
                    />
                  </h5>
                </div>
              </div>
            </div>
            <div className="align-items-start mb-5 d-flex justify-content-between trans-to">
              <div className="col-5 align-items-start">
                <div className="row">
                  <label className="label-1">User:</label>

                  {/*<input
                    className="input-acc"
                    type="text"
                    value={accountNumber}
                    onChange={(e) => {
                      setAccountNumber(e.target.value);
                    }}
                  />*/}
                </div>
              </div>
              <div className="col-6 align-items-start">
                <div className="row justify-content-between">
                  <label className="label-1">Name:</label>
                  <input
                    className="input-acc"
                    type="text"
                    name="name"
                    value={name}
                    onChange={(e) => {
                      setName(e.target.value);
                    }}
                  />
                </div>
                <div className="row justify-content-between">
                  <label className="label-1">Address:</label>
                  <input
                    className="input-acc"
                    type="text"
                    name="address"
                    value={address}
                    onChange={(e) => {
                      setAddress(e.target.value);
                    }}
                  />
                </div>
                <div className="row justify-content-between">
                  <label className="label-1">City:</label>
                  <input
                    className="input-acc"
                    type="text"
                    name="city"
                    value={city}
                    onChange={(e) => {
                      setCity(e.target.value);
                    }}
                  />
                </div>
                <div className="row justify-content-between">
                  <label className="label-1">Account Number:</label>
                  <input
                    className="input-acc"
                    type="number"
                    name="accountNumber"
                    value={accountNumber}
                    onChange={(e) => {
                      setAccountNumber(e.target.value);
                    }}
                  />
                </div>
                <div className="row justify-content-between">
                  <label className="label-1">Amount:</label>
                  <input
                    className="input-acc"
                    type="number"
                    name="amount"
                    value={amount}
                    onChange={(e) => {
                      setAmount(e.target.value);
                    }}
                  />
                </div>

                <button type="submit">Transferrr</button>
              </div>
            </div>
          </form>
        </>
      )}
    </div>
  );
};

export default Transactions;
